const SecretRecoveryPhrase = () => {
  const { strings } = useTranslations();
  return (
    <View style={styles.container}>
      <Box justifyContent="center" alignItems="center">
        <SvgXml xml={secretRecoveryPhraseXml} />
        <Text py="6" textAlign="center">
          Lorem Ipsum is simply dummy text of the printing and typesetting
          industry
        </Text>
      </Box>
      <Box
        flexDirection="row"
        rounded={50}
        bgColor="#1D1D1D"
        p="2"
        w="80"
        alignSelf="center"
        alignItems="center"
        justifyContent="space-between"
      >
        <Input
          placeholder={strings.start_typing}
          variant="unstyled"
          fontSize="md"
          color="#FFFFFF"
          keyboardAppearance="dark"
          px="4"
        />
        <Image source={Icons.inputSubmitIcon} alt="submitIcon" w="10" h="10" />
      </Box>
      <Box justifyContent="center" alignItems="center">
        <Text fontSize="xs" color="#757575">
          {strings.by_proceeding_you_agree_to_those}
        </Text>
        <HStack>
          <Text fontSize="xs" color="#CCAB2E" textDecorationLine="underline">
            {strings.terms}
          </Text>
          <Text fontSize="xs" color="#757575">
            {' '}
            {strings.and}{' '}
          </Text>
          <Text fontSize="xs" color="#CCAB2E" textDecorationLine="underline">
            {strings.conditions}
          </Text>
        </HStack>
      </Box>
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    justifyContent: 'space-between',
    alignItems: 'center',
    padding: 30
  },
  link: {
    color: '#CCAB2E'
  }
});

export default SecretRecoveryPhrase;
